INSERT IGNORE INTO `wp_k2bxp00xqc_posts` VALUES (3494, 1, '2014-12-29 11:16:45', '2014-12-29 19:16:45', '<h2>Intern Membership</h2>\r\n<h3>For trainees new to our coached disciplines.  We expect Interns to spend time developing their skills using individual and group coaching</h3>\r\n<p style="color: #222222;">Intern membership includes; Specific club member rates for all services, Access to members only group workout and training sessions.</p>\r\n<p>For pricing and availability please request the relevant PDF via the contact section.</p>\r\n<span class="hero-btn-holder text-center"><a class="hero-btn default booking" href="" target="_self">Pricing and Availability</a></span>\r\n\r\n<h2>Full Membership</h2>\r\n<h3>For trainees who successfully pass the full membership assessment</h3>\r\n<p style="color: #222222;">Full club members will be able to demonstrate competence to Motus Strength standards in specific areas of practice.  Full club membership includes; unsupervised use of the gymnasium (subject to conditions), specific club member rates for all services, access to members only coaching and workout sessions.</p>\r\n<p>For pricing, availability and assessment details please request the relevant PDF via the contact section. </p>\r\n<span class="hero-btn-holder text-center"><a class="hero-btn default booking" href="" target="_self">Pricing and Availability</a></span>', 'Membership Information', '', 'inherit', 'closed', 'open', '', '3414-revision-v1', '', '', '2014-12-29 11:16:45', '2014-12-29 19:16:45', '', 3414, 'http://localhost/motus/?p=3494', 0, 'revision', '', 0); 
INSERT IGNORE INTO `wp_k2bxp00xqc_posts` VALUES (3495, 1, '2014-12-29 11:17:37', '2014-12-29 19:17:37', '<h2 style="text-align: center;">Intern Membership</h2>\r\n<h3 style="text-align: center;">For trainees new to our coached disciplines. We expect Interns to spend time developing their skills using individual and group coaching</h3>\r\n<p style="color: #222222; text-align: center;">Intern membership includes; Specific club member rates for all services, Access to members only group workout and training sessions.</p>\r\n<p style="text-align: center;">For pricing and availability please request the relevant PDF via the contact section.</p>\r\n<p style="text-align: center;"><span class="hero-btn-holder text-center"><a class="hero-btn default booking" target="_self">Pricing and Availability</a></span></p>\r\n\r\n<h2 style="text-align: center;">Full Membership</h2>\r\n<h3 style="text-align: center;">For trainees who successfully pass the full membership assessment</h3>\r\n<p style="color: #222222; text-align: center;">Full club members will be able to demonstrate competence to Motus Strength standards in specific areas of practice. Full club membership includes; unsupervised use of the gymnasium (subject to conditions), specific club member rates for all services, access to members only coaching and workout sessions.</p>\r\n<p style="text-align: center;">For pricing, availability and assessment details please request the relevant PDF via the contact section.</p>\r\n<p style="text-align: center;"><span class="hero-btn-holder text-center"><a class="hero-btn default booking" target="_self">Pricing and Availability</a></span></p>', 'Membership Information', '', 'inherit', 'closed', 'open', '', '3414-revision-v1', '', '', '2014-12-29 11:17:37', '2014-12-29 19:17:37', '', 3414, 'http://localhost/motus/?p=3495', 0, 'revision', '', 0); 
INSERT IGNORE INTO `wp_k2bxp00xqc_posts` VALUES (3496, 1, '2014-12-29 11:19:42', '2014-12-29 19:19:42', '<p style="text-align: center;">Coaching will be available through three formats;</p>\r\n[ut_service_icon_box color="#dddddd" hovercolor="#930904" icon="fa-users" headline="Group Coaching" width="third" class="add-bottom"] Learn, develop and master specific movement and conditioning skills in a structured group setting. Group coaching will involve exercise class based format with a specific focus and targeted ability requirement. Use the Booking section for information on each class and availability. [/ut_service_icon_box]\r\n\r\n[ut_service_icon_box color="#dddddd" hovercolor="#930904" icon="icon-group icomoon-font" headline="Group Workout" width="third" class="add-bottom"] Train and develop specific fitness and performance components in a group setting. Each workout will test performance and include competitive elements. Use the booking section for more information on each class and availability. [/ut_service_icon_box]\r\n\r\n[ut_service_icon_box color="#dddddd" hovercolor="#930904" icon=" fa-user" headline="Individual Coaching" width="third" last="true" class="add-bottom"] For the trainees requiring individual service and program design. The most effective way to work with us, especially when combined with Group Coaching. For coach availability please contact us ASAP. [/ut_service_icon_box]', 'Training With Us', '', 'inherit', 'closed', 'open', '', '3304-revision-v1', '', '', '2014-12-29 11:19:42', '2014-12-29 19:19:42', '', 3304, 'http://localhost/motus/?p=3496', 0, 'revision', '', 0); 
